mapper.select:

	select  count(*)
	from record
	where course_no = #{courseNo}

select record_no recordNo , course_no courseNo , r.user_no userNo , name , review , weather , temperature , course_category courseCate , course_time courseTime , difficulty , to_char(reg_date, 'YY.MM.DD HH:Mi') regDate
from record r, users u where r.user_no = u.user_no and course_no = #{courseNo}
and r.user_no = ${authUserNo}

select course_no ,record_img_no ,ri.record_no ,save_name ,file_path ,order_no from record_img ri, record r where ri.record_no = r.record_no and course_no = #{courseNo}
and r.user_no = ${authUserNo}
order by record_no, record_img_no, order_no
