mapper.select:

	select  count(*)
	from record
	where course_no = #{courseNo}

select record_no recordNo , course_no courseNo , r.user_no userNo , name , review , weather , temperature , course_category courseCate , course_time courseTime , difficulty , to_char(reg_date, 'YY.MM.DD HH24:Mi') regDate
from record r, users u where r.user_no = u.user_no and course_no = #{courseNo}
and r.user_no = ${authUserNo}

mapper.update:

	update record
	set review = #{review}
		,weather = #{weather}
		,temperature = #{temperature}
		,course_category = #{courseCate}
		,course_time = #{courseTime}
		,difficulty = #{difficulty}
		,reg_date = to_date(#{regDate}, 'yyyy-mm-dd hh24:mi')
	where record_no = #{recordNo}
